import React from 'react';
import OrderForm from './form_order';

const orders = [
    {
      name: "Заказ 1",
      description: "Заказ за 12.07.2023",
      key: 1,
      price: "30$"
    },
    {
      name: "Заказ 2",
      description: "Заказ за 13.07.2023",
      key: 2,
      price: "120$"
    },
    {
      name: "Заказ 3",
      description: "Заказ за 14.07.2023",
      key: 3,
      price: "60$"
    }
  ];

class Main extends React.Component{
  constructor(){
    super();
    this.rows = [];
    this.orders = orders;
    this.state = {
      isClickedOrderButton: false,
      isClickedIndexPageButton: false
    }
  }
  onClickOrderButton(){
    this.setState({
      isClickedOrderButton: true,
      isClickedIndexPageButton: false
    });
  }
  onClickedIndexPageButton(){
    this.setState({
      isClickedOrderButton: false,
      isClickedIndexPageButton: true
    });
  }
  IndexPageButton(){
    return(
      <button className="btn btn-outline-secondary mt-3 btn-lg" onClick={this.onClickedIndexPageButton}>
        <svg xmlns="http://www.w3.org/2000/svg" width="2rem" height="2rem" fill="currentColor" className="bi bi-arrow-left-square-fill" viewBox="0 0 16 16">
          <path d="M16 14a2 2 0 0 1-2 2H2a2 2 0 0 1-2-2V2a2 2 0 0 1 2-2h12a2 2 0 0 1 2 2v12zm-4.5-6.5H5.707l2.147-2.146a.5.5 0 1 0-.708-.708l-3 3a.5.5 0 0 0 0 .708l3 3a.5.5 0 0 0 .708-.708L5.707 8.5H11.5a.5.5 0 0 0 0-1z"></path>
        </svg>
      </button>
    );
  }
  App(name, description, price){
    return(
      <div className="col-12 col-sm-10 col-md-8 panels-col-lg-3 col-lg-3 order-4 d-flex flex-column align-items-center justify-content-start panelShadow panelWidth panelOther">
      <p className="mt-3 h3">{name}</p>
      <p className="text-break">{description}</p>
      <button 
      onClick={()=>(this.onClickOrderButton())} 
      className="btn btn-secondary order-button m-3">{price}</button>
    </div>
    );
  }
  onRows(){
    for (let i = 0; i < orders.length; i++) {
      this.rows.push(
          this.App(this.orders[i].name, this.orders[i].description, this.orders[i].price)
        );
    }
    return(
    <div className="container">
      <div className="row pt-3 d-flex justify-content-center">
        {this.rows}
      </div> 
    </div>
    );
  }
  render(){
    if(!this.state.isClickedOrderButton) return(IndexPageButton(), <OrderForm />);
    else if(!this.state.isClickedIndexPageButton) return(this.onRows());


    // if (!this.state.isClickedOrderButton){
    // return (
    //   <div className="container">
    //     <div className="row pt-3 d-flex justify-content-center">
    //         {this.onRows()}
    //       </div> 
    //  </div>
    // );
    // }else{
    //   return(<OrderForm />);
    // }
  }
}

export default Main;
