      price: "30$",
      button: function(){}
    },
    {
      name: "Заказ 2",
      description: "Заказ за 13.07.2023",
      key: 2,
      price: "120$",
      button: function(){}
    },
    {
      name: "Заказ 3",
      description: "Заказ за 14.07.2023",
      key: 3,
      price: "60$",
      button: function(){}
    }
  ];

class Main extends React.Component{
  constructor(props){
    super(props);
    this.props = props;
    this.rows = [];
    this.orders = orders;
    this.state = {isOrderButtonOn: false, isProfileButtonOn: false};
    this.handleClickOrderButton = this.handleClickOrderButton.bind(this);
  }

  handleClickOrderButton() {
    this.setState(prevState => ({
      isOrderButtonOn: !prevState.isOrderButtonOn
    }));
  }
  onRows(){
    for (let i = 0; i < orders.length; i++) {
      this.orders[i].button = this.handleClickOrderButton;
      this.rows.push(
          <App {...this.orders[i]} />
        );
    }
    return(
    <div className="container">
      <div className="row pt-3 d-flex justify-content-center">
        {this.rows}
      </div> 
    </div>
    );
  }
