import React from 'react';

const orders = [
    {
      name: "Заказ 1",
      description: "Заказ за 12.07.2023",
      key: 1,
      price: "30$"
    },
    {
      name: "Заказ 2",
      description: "Заказ за 13.07.2023",
      key: 2,
      price: "120$"
    },
    {
      name: "Заказ 3",
      description: "Заказ за 14.07.2023",
      key: 3,
      price: "60$"
    }
  ];

class Main extends React.Component{
  constructor(props){
    super(props);
    this.rows = [];
    this.orders = orders;
    this.isOnClck = true;
  }
  onClickButton(){
    this.isOnClck = false;
  }
  App(name, description, price){
    return(
      <div className="col-12 col-sm-10 col-md-8 panels-col-lg-3 col-lg-3 order-4 d-flex flex-column align-items-center justify-content-start panelShadow panelWidth panelOther">
      <p className="mt-3 h3">{name}</p>
      <p className="text-break">{description}</p>
      <button onClick={this.onClickButton} className="btn btn-secondary order-button m-3">{price}</button>
    </div>
    );
  }
  onRows(){
    for (let i = 0; i < orders.length; i++) {
      this.rows.push(
          this.App(this.orders[i].name, this.orders[i].description, this.orders[i].price)
        );
    }
    return(this.rows)
  }
  render(){
    if(this.isOnClck){
    return (
      <div className="container">
        <div className="row pt-3 d-flex justify-content-center">
            {this.onRows()}
          </div> 
     </div>
    )
  }
}
  // set rows(App){
  //   for (let i = 0; i < orders.length; i++) {
  //         this.rows.push(<App {...orders[i]} />)
  //     }
  // }
    // rows = [];
    // for (let i = 0; i < orders.length; i++) {
    //     rows.push(<App {...orders[i]}/>);
    // }
    // return (
    //   <div className="container">
    //     <div className="row pt-3 d-flex justify-content-center">
    //         {rows}
    //       </div> 
    //  </div>
    //  );
}

export default Main;
