import React from 'react';
import OrderForm from './form_order';
import App from './App';
import Navbar from './navbar'
import Footer from './footer'

const orders = [
    {
      name: "Заказ 1",
      description: "Заказ за 12.07.2023",
      key: 1,
      price: "30$",
      button: function(){}
    },
    {
      name: "Заказ 2",
      description: "Заказ за 13.07.2023",
      key: 2,
      price: "120$",
      button: function(){}
    },
    {
      name: "Заказ 3",
      description: "Заказ за 14.07.2023",
      key: 3,
      price: "60$",
      button: function(){}
    }
  ];

class WebPage extends React.Component{
  constructor(){
    super();
    this.rows = [];
    this.orders = orders;
    this.state = {isOrderButtonOn: false, isProfileButtonOn: false};
    this.handleClickOrderButton = this.handleClickOrderButton.bind(this);
  }
  handleClickOrderButton() {
    this.setState(prevState => ({
      isOrderButtonOn: !prevState.isOrderButtonOn
    }));
  }
  onRows(){
    for (let i = 0; i < orders.length; i++) {
      this.orders[i].button = this.handleClickOrderButton;
      this.rows.push(
          <App {...this.orders[i]} />
        );
    }
    return(
    <div className="container">
      <div className="row pt-3 d-flex justify-content-center">
        {this.rows}
      </div> 
    </div>
    );
  }
  render(){
    if(this.state.isOrderButtonOn) return(<><Navbar /> <main className='m-auto'><OrderForm /></main> <Footer /></>)
    else return(this.onRows())
  }
}
export default WebPage;
