import React from 'react';
import OrderForm from './form_order';

const orders = [
    {
      name: "Заказ 1",
      description: "Заказ за 12.07.2023",
      key: 1,
      price: "30$"
    },
    {
      name: "Заказ 2",
      description: "Заказ за 13.07.2023",
      key: 2,
      price: "120$"
    },
    {
      name: "Заказ 3",
      description: "Заказ за 14.07.2023",
      key: 3,
      price: "60$"
    }
  ];

class Main extends React.Component{
  constructor(props){
    super(props);
    this.rows = [];
    this.orders = orders;
    this.state = {
      isClickedOrderButton: false,
    }
  }
  onClickOrderButton(){
    this.state.isClickedOrderButton ? this.setState({isClickedOrderButton: false}) : this.setState({isClickedOrderButton: true});
  }
  App(name, description, price){
    return(
      <div className="col-12 col-sm-10 col-md-8 panels-col-lg-3 col-lg-3 order-4 d-flex flex-column align-items-center justify-content-start panelShadow panelWidth panelOther">
      <p className="mt-3 h3">{name}</p>
      <p className="text-break">{description}</p>
      <button 
      onClick={()=>(this.onClickOrderButton())} 
      className="btn btn-secondary order-button m-3">{price}</button>
    </div>
    );
  }
  onRows(){
    for (let i = 0; i < orders.length; i++) {
      this.rows.push(
          this.App(this.orders[i].name, this.orders[i].description, this.orders[i].price)
        );
    }
    return(
    <div className="container">
      <div className="row pt-3 d-flex justify-content-center">
        {this.rows}
      </div> 
    </div>
    );
  }
  render(){
    if(!this.state.isClickedOrderButton) return(<><this.IndexPageButton /> <OrderForm /></>);
    else return(<this.onRows />);
  }
}

export default Main;
